#!/bin/bash

for f in $(seq -w 39); do
    # note indentation MUST be tabs. heads up in case your cut & paste translates into spaces...
    cat <<- END > useref.$f.html
        <html>
            <head>
                <!-- build:css useref.$f.min.css -->
                <link rel="stylesheet" type="text/css" href="useref.$f.css"/>
                <!-- endbuild -->
            </head>
            <body></body>
        </html>
END

    touch useref.$f.css
done
